import React from 'react';
import logo from '../assets/logo.svg';
import "../css/styles.css";
import {PowerBIEmbed} from "powerbi-client-react";
import {motion, useScroll, useSpring, useTransform, MotionValue} from 'framer-motion';
import { useRef } from 'react';

function Parallax(value: MotionValue<number>, distance: number) {
  return useTransform(value, [0, 1], [-distance, distance]);
}

function Slides({ id }: { id: number }) {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });
  const y = Parallax(scrollYProgress, 300);

  return (
    <section>
      <div ref={ref}>
        <img src={`./${id}.jpg`} alt="test pictures" />
      </div>
      <motion.h2 style={{ y }}>{`Some texts about chart number: ${id}`}</motion.h2>
    </section>
  );
}

export default function App() {
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <>
    <div className="title">
      
        <h1>The graph below is the embedded one.</h1>
      
    </div>
      {[1, 2, 3, 4].map((image) => (
        <Slides id={image} />
      ))}
      <motion.div className="progress" style={{ scaleX }} />
    </>
  );
}
